mapper.insert:
INSERT INTO PAYMENTS_BILL( BILL_NO ,PLAN_NO ,USER_ID ,PAIDDATE ,ENDDATE ) VALUES( PAYMENTS_BILL_SEQ.NEXTVAL , 1 , #{userId} , SYSDATE , ADD_MONTHS(SYSDATE, +1)
)
SELECT LAST_NUMBER FROM USER_SEQUENCES WHERE SEQUENCE_NAME = 'PAYMENTS_BILL_SEQ'

mapper.select:
SELECT ENDDATE
FROM PAYMENTS_BILL
WHERE USER_ID = #{userId}

SELECT PLAN_NO
FROM PAYMENTS_BILL
WHERE USER_ID = #{userId}

SELECT MAX_UPLOAD_CAPACITY
FROM PAYMENTS_PLAN
WHERE PLAN_NO = #{planNo}

SELECT COUNT(*)
FROM PAYMENTS_BILL
WHERE USER_ID = #{userId}

SELECT MEMORY_CAPACITY
  FROM PAYMENTS_PLAN
 WHERE PLAN_NO = #{planNo}
